from abc import ABC, abstractmethod
from typing import Any

class SPStrategy(ABC):
    "Abstract base class for switch point strategy implementations."

    def __init__(self):
        self.data = []

    def feed(self, data: Any) -> None:
        self.data.append(data)

    @abstractmethod
    def should_switch(self) -> bool:
        "Determine if s should be switched"


class TestStrategy(SPStrategy):

    def __init__(self):
        super(TestStrategy, self).__init__()

    def should_switch(self) -> bool:
        return False
